//逻辑关系位置
function displayPathSpan(xLev, yLev, type)
{
	 switch (type) {
    case "and":
        type = "并且";
        break; 
    case "or":
        type = "或者";
        break; 
    default: 
    } 

	 var left=(xLev-1)*75+5;
	 var top=(yLev-1)*30+5;
	 var rtn= "<span id='"+xLev+"*"+yLev+"' class='btn btn-default dropdown-toggle rule-join-container' style='position: absolute; left: "+left+"px; top: "+top+"px;'>";
	 rtn=rtn+ " <span style='font-size: 11pt'>"+type+"</span><i class='glyphicon glyphicon-chevron-down rule-join-node'></i></span>";
	 return rtn;
}

//内容位置
function displayPathCon(xLev, yLev, con)
{
	  //alert(xLev+"**"+yLev)
	  var op=con.op;
	  switch (con.op) {
		  case "GreaterThen":
        op = "大于";
        break; 
		  case "GreaterThenEquals":
        op = "大于或等于";
        break;
    case "Equals":
        op = "等于";
        break;  
    case "NotEquals":
        op = "不等于";
        break;
    case "Between":
        op = "在";
        break;
    case "In":
        op = "在集合";
        break;
    case "LessThen":
        op = "小于";
        break;
    case "LessThenEquals":
        op = "小于或等于";
        break; 
    default: 
   } 
		 var left=xLev*75+5;
	  var top=(yLev-1)*30+10;
		 var htmlOne="<div style='position: absolute; left: "+left+"px; top: "+top+"px;'>"
			htmlOne=htmlOne+"	<span style='font-size:12px'>"
			var Label=con.left.leftPart.variableLabel;
			if(Label!="")Label="的"+Label;
			htmlOne=htmlOne+"  <span class='line-darkcyan'>"+con.left.leftPart.variableCategory+Label+"</span>"
			htmlOne=htmlOne+"  <span class='line-red'>"+op+"</span>"
			if(con.value.valueType=="Constant"){
					htmlOne=htmlOne+"		<span class='line-blue'>"+con.value.constantLabel+"</span>"
			}else{
				 if(((con.op=="Between")||(con.op=="In")&&(con.value.contentLimit!=""))){ //Between和集合的limit不为空时:有括号，有区间
					  htmlOne=htmlOne+"		<span class='line-brown'>("+con.value.content+"-"+con.value.contentLimit+")</span>"
					}else{
						 htmlOne=htmlOne+"		<span class='line-brown'>"+con.value.content+"</span>"
					}
			  htmlOne=htmlOne+"		<span class='line-blue'>"+con.value.contentUomDesc+"</span>"
			}
			if(con.op=="Between")htmlOne=htmlOne+"		<span class='line-red'>之间</span>"
   if(con.op=="In")htmlOne=htmlOne+"		<span class='line-red'>之中</span>"

			htmlOne=htmlOne+"	</span>"
			htmlOne=htmlOne+"	<i class='glyphicon glyphicon-trash line-i'></i>"
			htmlOne=htmlOne+"</div>"
			return htmlOne;
}

